import React from "react";

export default function Header(){
    const nav = [
        {text:'Home',id:0},
        {text:'Products',id:1},
        {text:'About',id:2},
        {text:'Contact',id:3}
    ]

    const navReturn=(text)=>{
        if(text==='Home'){
            return '#'+text
        }else if(text==='Products'){
            return '#'+text
        }else return '##'
    }
    return(
        <header>
            <div className="logoP">
                <img className="logo" src="imgs/logo/logo.png" alt="logo"/>
            </div>
            <nav className="nav1">
                {nav.map(e=><a key={e.id} href={navReturn(e.text)}>{e.text}</a>)}
            </nav>
            <nav className="nav2">
                <a href="##">
                    orders
                    <span>{/*items.length*/}</span>
                </a>
            </nav>
        </header>
    )
}
