import React, { Fragment } from "react";

class Home extends React.Component {
    constructor(props){
        super(props);
        this.state={}
    }
    render(){
        return(
            <Fragment>
                <div id="bg">
                    <header>
                        <div className="logoP">
                            <img className="logo" src="imgs/logo/logo.png" alt="logo"/>
                        </div>
                        <nav>
                            {this.props.nav.map((e,i)=><a key={i} href={e==='Home'?'#bg':(e==='Products'&&'#content')}>{e}</a>)}
                        </nav>
                    </header>
                    <div className="bodyDiv">
                        <div className="textP">
                            <h1>TIMBERLANDÂ® PREMIUM SHOES</h1>
                        </div>
                        <div className="shoeE">
                            <img src="imgs/img/shoesErase1.png" alt="shoe"/>
                        </div>
                    </div>
                </div>
                <div id="content">
                    {this.props.objs.map((e,i)=>
                        <div key={i} className="shoesP">
                            <div>
                                <img src={e.src} alt=""/>
                            </div>
                            <div>
                                <p>{e.price}$</p>
                                <p>{e.desc}</p>
                                <input type='button' value='Add To Cart'/>
                            </div>
                        </div>
                    )}
                </div>
            </Fragment>
        )
    }
}

export default Store;
